import React, {Component} from 'react';
import { View, Text, StyleSheet, ScrollView, Image, TouchableOpacity, Flatlist } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons'
import { FlatList } from 'react-native-gesture-handler';

const dataList = [{key: '1'}, {key: '2'}, {key: '3'}]

export default class Home extends Component {

  _renderItem = ({item, index}) =>{
    let {itemStyle, txtItem} = styles
    return(
      <View style={itemStyle}>
        <Text style={txtItem}>{item.key}</Text>
      </View>
    )
  }


  render(){
    return (
      <View style={styles.container}>

            <View style={styles.area}>
              <View style={styles.header}>
                <View style={styles.txtContainer}>
                  <Text style={styles.txt}>Tecnologia Bluetooth</Text>
                </View>
              </View>

              <View style={styles.line}/>



              <View style={styles.description}>
                <View style={{alignItems: 'center'}}>
                  <Image
                      source={{uri: 'https://s2.glbimg.com/xREAsoZIeGv8sVjIGUt2U_St-vw=/0x0:300x312/640x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_08fbf48bc0524877943fe86e43087e7a/internal_photos/bs/2021/f/M/LxdDJBTi2x9gCwhsqX0g/2012-01-24-bluetooth.jpg'}}
                      style={styles.logo}
                  />

                  <Text style={styles.txtDescription}>
                    O Bluetooth é uma tecnologia de comunicação sem fio desenvolvida pela empresa de comunicação Ericsson, em 1994. 
                    Ela permite a troca de dados e arquivos entre celulares, computadores, scanners, fones de ouvido e demais 
                    dispositivos de forma rápida e segura. [...]
                  </Text>
                </View>


                <View style={{alignItems: 'flex-end', marginTop: 10}}>
                  <TouchableOpacity style={styles.buttom}>
                    <Text style={{color: 'white', fontSize: 15}}>Leia mais</Text>
                  </TouchableOpacity>
                </View>
              </View>

            </View>



            <View>
              <View style={styles.txtContainer}>
                <Text style={styles.txt}>Fones Bluetooth</Text>

                <TouchableOpacity style={{position: 'absolute', right: 0, alignSelf: 'center'}}>
                  <MaterialIcons 
                    name="filter-list"
                    size={24}
                    color="#000"
                  />                  
                </TouchableOpacity>
              </View>

              <FlatList
                data={dataList}
                renderItem={this._renderItem}
                keyExtractor={(item, index) => index.toString()}
              />

            </View>

        </View>
      );
  }
}
const styles = StyleSheet.create({
  container:{
    flex: 1,
    width: '100%',
    backgroundColor: '#fff'
  },
  header:{
    backgroundColor: '#f17',
  },
  logo:{
    width: 100,
    height: 100,
    margin: 10,
    padding: 3
  },
  txtContainer:{
    flexDirection: 'row',
    marginVertical: '5%',
    marginHorizontal: '5%',
  },
  txt:{
    fontFamily: 'BebasNeue_400Regular',
    fontSize: 26,
    marginHorizontal: '1%'
  },
  description:{
    margin: 2,
    padding: 5,
  },
  txtDescription:{
    fontSize: 16,
  },
  buttom:{
    backgroundColor: 'black',
    width: 80,
    height: 30,
    justifyContent:'center',
    alignItems: 'center',
    borderRadius: 5
  },
  itemStyle:{
    backgroundColor: 'blue',
    alignItems: 'center',
    justifyContent: 'center',
    height: 100,
    flex: 1
  },
  txtItem:{
    color: 'white',
    fontsize: 50,
  },
  line:{
    borderBottomcolor: '#D8d8d8',
    borderBottomWidth: 2,
  } 
});
